import React, { useState } from 'react';
import { Send } from 'lucide-react';
import useWebSocket from '../hooks/useWebSocket';

const ChatInput = () => {
  const [newMessage, setNewMessage] = useState('');
  const { sendMessage, isConnected } = useWebSocket();

  const handleSendMessage = () => {
    if (newMessage.trim() && isConnected) {
      const success = sendMessage(newMessage);
      if (success) {
        setNewMessage('');
      }
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="bg-white rounded-b-2xl shadow-lg p-4">
      <div className="flex gap-3">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder={isConnected ? "Type a message..." : "Connecting..."}
          disabled={!isConnected}
          className="flex-1 px-4 py-3 border-2 border-gray-200 rounded-lg focus:outline-none focus:border-indigo-500 transition-colors disabled:bg-gray-100"
        />
        <button
          onClick={handleSendMessage}
          disabled={!isConnected || !newMessage.trim()}
          className="bg-indigo-600 text-white p-3 rounded-lg hover:bg-indigo-700 transition-colors shadow-md disabled:bg-gray-400 disabled:cursor-not-allowed"
        >
          <Send className="w-6 h-6" />
        </button>
      </div>
      {!isConnected && (
        <p className="text-xs text-red-500 mt-2">Disconnected from server</p>
      )}
    </div>
  );
};

export default ChatInput;
